INSERT INTO users(name, surnames, email, password)
INSERT INTO colles (name, uni, description, phoneNumber, email, web, address, color, img_path)
VALUES
('Al·lots de Llevant', false, 'Els Al·lots de Llevant som una colla castellera sorgida a Manacor l’any 1996 fruit de la il·lusió i curiositat d’un grup d’amics que, a la recerca d’una activitat de lleure que els satisfés, començaren a assajar castells enmig del boom que aquesta afició va tenir a mitjans dels anys noranta.', '+34687967644', 'allotsdellevant@gmail.com', 'http://al-lotsdellevant.cat', 'Carrer nou, 35, Manacor', '#52C85C', '~/images/colles/alotsllevant.png'),
('Angelets del Vallespir', false, 'Volem promoure la cultura catalana fent castells. És una tradició popular catalana molt antiga on tothom pot participar: homes i dones, joves i vells, petits i grans. Tots seu els benvinguts en la nostra colla castellera. La nostra activitat consisteix a aixecar castells, això durant les festes majors o en qualsevol altra manifestació de tipus cultural o esportiu. Els castells són construccions humanes amb arquitectures complexes i precises per intentar pujar el més amunt possible. Per fer això cal molta cohesió, solidaritat i organització entre el grup de castellers.', '+33468873680', 'secretaris.colla@cmail.cat', 'http://sites.google.com/site/angeletsdelvallespir/', 'Mairie, square Guy Malé, Sant Joan de Pladecorts', '#3B5F7B', '~/images/colles/angeletsvallespir.png'),
('Bordegassos de Vilanova', false, 'Des del 1972 la capital del Garraf compta amb colla castellera pròpia: els Bordegassos de Vilanova. La colla de la camisa de color groc terrós és l’única que ha assolit els nou pisos després d’haver descarregat tota la gamma de vuit. Però també va ser la primera, l’any 1979, que va coronar un castell de vuit amb una nena al pom de dalt. Durant aquests gairebé 40 anys els castellers vilanovins les han vist de tots colors, però sempre han acabat remuntant el vol.', '+34639436748', 'bordegassos@bordegassos.org', 'http://www.bordegassos.org/', 'Plaça de les Casernes 2, Apartat de correus 399, Vilanova i la Geltrú', '#FFCC01', '~/images/colles/bordegassos.png'),
('Brivalls de Cornudella', false, 'Tot va començar la Nit de Nadal de l’any 1975 en el marc d’una xocolatada popular a Cornudella. Els joves del poble, engrescats per la festa, van començar a enfilar-se els uns damunt dels altres. Un dels presents a l’acte, el conegut cantant de Folk Jaume Arnella, en veure-ho, va proposar a tot aquell jovent d’anar més enllà i aprendre a fer castells. La idea va ser ven rebuda i el 6 de gener de 1976, després de rebre les primeres lliçons pràctiques de la mà del vilafranquí Pep Miralles, va néixer la primera i única colla castellera moderna del Priorat.', null, 'brivalls2012@gmail.com', 'https://brivallsdecornudella.wordpress.com/', 'C/Comte de Rius, 10. Cornudella de Montsant (Tarragona), 43360', '#010080', '~/images/colles/brivalls.png'),
('Capgrossos de Mataró', false, 'Els Capgrossos, la primera colla castellera del Maresme, i amb clara vocació des dels seus inicis de superar l’encasellament identificatiu amb la capital. Ens sentim representants de cada poble de la comarca i, quan hi actuem en els diferents pobles i viles que ens ho demanen, notem aquella plaça com a pròpia: actuem a casa. Aquest caràcter comarcal s’exemplifica en el fet que molts dels més de 1.000 socis de la colla provenen de poblacions maresmenques més o menys properes com ara Argentona, Sant Andreu de Llavaneres, Premià de Mar, Vilassar de Mar, Cabrils, Canet de Mar, Sant Cebrià de Vallalta, etcètera.', '+34647625324', 'colla@capgrossos.cat', 'http://www.capgrossos.cat/', ' Ptge Mariona Galindo i Lora, 1 08301 - Mataró', '#1C3776', '~/images/colles/capgrossosmataro.png'),
('Castellers d’Altafulla', false, null, '+34626053718', 'castellers@tinet.cat', 'http://www.castellersaltafulla.cat/', 'Carrer de les Eres, s/n, Altafulla ', '#9B8EE0', '~/images/colles/castellersaltafulla.png'),
('Castellers d’Andorra', false, 'Tres persones amb ganes de fer castells, per internet i les xarxes socials, es van animar a muntar la colla. Pocs mesos després, i per veure fins a quin punt podria interessar a la gent, es convocà “la crida”, una reunió per informar a tothom que hi pogués estar interessat. Va ser un dimarts, 17 de juny de 2014, i ja es van apuntar a la colla unes 25 persones.', '+376 384 923', 'castellers.andorra@gmail.com', 'http://www.castellersandorra.com', 'C/ de la Plana s/n, Santa Coloma, Andorra la Vella ', '#FFFFFF', '~/images/colles/castellersandorra.png'),
('Castellers d’Esparreguera', false, 'La Colla dels Castellers d’Esparreguera es va fundar el 1994 i des d’aleshores ha assolit gairebé tota la gamma de castells de set pisos, sent el seu màxim registre el 2 de 7 carregat. Han descarregat construccions com el 9 de 6 amb una sola enxaneta, el 3 de 6 aixecat per sota, el 4 de 7 amb l’agulla, el pilar de cinc sense mans i el 5 de 7 i han provat a plaça el pilar de 6. El nombre dels seus components s’ha estabilitzat en unes 150 persones. Aquesta entitat, oberta a tothom, ideal per gaudir-la en família, amb els amics o per als qui tinguin ganes de conèixer gent, realitza unes 25 actuacions l’any, a més de diverses activitats culturals i lúdiques.', '+34658851751', 'castellersesparreguera@gmail.com', 'https://webbordeus.wordpress.com/', 'Apartat de correus 174, Esparreguera-08292', '#902E55', '~/images/colles/castellersesparraguera.png'),
('Castellers d’Esplugues', false, null, '+34650946159', 'cargolins@cargolins.cat', 'http://www.cargolins.cat', 'Carrer de Josep Argemí 49, 08950, Esplugues de Llobregat (Barcelona)', '#11559C', '~/images/colles/castellersesplugues.png'),
('Castellers de Badalona', false, 'Els Castellers de Badalona som una de les colles castelleres més joves, concretament la 57ª de les 60 integrades a la Coordinadora de Colles Castelleres de Catalunya. El nostre objectiu fonamental és lògicament fer castells i representar Badalona, que és tot un orgull per a nosaltres. Significa no acceptar que la nostra ciutat sigui un mer satèl·lit metropolità i lluitar pel dinamisme cultural de Badalona. També suposa no limitar la colla al centre de la ciutat. En aquest sentit, no només s’estrenen castells a la Plaça de la Vila, sinó també al barri d’Artigues, Llefià, Casagemes, Coll i Pujol etc…', '+34630565595', 'rrpp@castellersdebadalona.cat', 'http://www.castellersdebadalona.cat/', 'Can Peixau c\Font i Escolà 42, BDN', '#FCBF49', '~/images/colles/castellersbadalona.png'),
('Castellers de Barcelona', false, 'Els Castellers de Barcelona vam ser fundats a Barcelona l’any 1.969 per un grup d’aficionats provinents de les terres de Catalunya on aquesta tradició estava més arrelada, i amb el seu esforç i il·lusió van aconseguir convertir una nova colla en el que ara és: un referent dins la cultura popular catalana d’arrel tradicional. Amb la nostra camisa vermella sempre estem disposats a realitzar les nostres construccions amb una barreja de força, equilibri, valor i seny.', '+34934982728', 'colla@castellersdebarcelona.cat', 'http://www.castellersdebarcelona.cat', 'Carrer Bilbao, 212-214 08018 Barcelona', '#D9001D', '~/images/colles/castellersbarcelona.png'),
('Castellers de Berga', false, null, null, 'castellersdeberga@gmail.com', 'http://castellersdeberga.blogspot.com.es', 'Cine Catalunya Carrer Ciutat 35 - Berga', '#233881', '~/images/colles/castellersberga.png'),
('Castellers de Caldes de Montbui', false, 'Ser casteller sovint no és fàcil, requereix temps, dedicació, moltes ganes, empenta i perseverança. Tot i així, un cop a dins, descobreixes un món nou. Un món de treball en equip, d’objectius comuns, d’amistats, de gresca i diversió i de felicitat compartida quan veus que els esforços de tots han merescut la pena.  Si tens curiositat per conèixer la colla, no ho dubtis, t’esperem amb els braços oberts.', '+34619573110', 'colla@castellersdecaldes.cat', 'https://sites.google.com/site/castellerscaldesmontbui', 'Carrer General Padrós nº35 - 08140 Caldes de Montbui', '#007C1F', '~/images/colles/castellerscaldes.png'),
('Castellers de Castellar del Vallès', false, 'La colla es va començar a gestar la tardor de l’any 2011. Un grup de persones van tenir la iniciativa de parlar per crear un grup que tirés endavant una entitat de castellers actius en el municipi de Castellar a on no hi havia una colla castellera. Es va fer una crida a la gent del poble per veure la viabilitat del projecte. Al tenir una gran resposta per part dels ciutadans,  (d’aquesta primera crida es van fer ressò a prop d’una seixantena de persones), es van posar en contacte amb la Coordinadora de Colles Castelleres de Catalunya, amb l’objectiu de veure la possibilitat  de crear-la i per això es va tirar endavant el projecte.  Al poc temps ja érem 100. D’aquesta forma va néixer la primera colla castellera a la Vila.', '+34616995713', 'junta@capgirats.cat', 'http://capgirats.cat/', 'Plaça del Mirador s/n, 08211 – Castellar del Vallès', '#8A0F11', '~/images/colles/castellerscastellar.png'),
('Castellers de Castelldefels', false, 'La colla castellera de Castelldefels fa la seva primera sortida el 31 de maig de 1982, a l’Aplec de Bruguers, on descarrega el 3de6, el 4de6 i un pilar de 4. No es pot parlar del naixement dels Castellers de Castelldefels sense tenir en compte que va coincidir amb l’inici dels Grallers i Geganters, que van fundar l’Agrupació de Cultura Popular de Castelldefels, entitat que agafa la responsabilitat de l’organització de les festes majors de la ciutat, així com de la recuperació i difusió de la cultura popular. Paco López, el primer cap de colla del Castellers de Castelldefels, és qui encapçala aquest projecte de fundació dels castellers i de la resta d’entitats de l’Agrupació.', '+34652959941', 'info@elsgrocs.cat', 'www.elsgrocs.cat', 'Av. Lluís Companys, 16 Castelldefels', '#FFFF23', '~/images/colles/castellerscastelldefels.png'),
('Castellers de Cerdanyola', false, 'La colla castellera es va crear a partir d’unes converses durant el mes de juny de 1998, quan unes deu persones de diferent procedència, que s’anaven trobant a un conegut cafè de tertúlia de la ciutat, situat al Carrer Sant Ramon (el Bar Grau), van posar els fonaments de la colla. Cal destacar, que en poc temps, han assolit una àmplia gamma de castells de 7 i 7 i mig, i en una zona geogràfica sense gaire tradició castellera.', '+34699990058', 'info@castellersdecerdanyola.cat', 'http://castellersdecerdanyola.cat', 'Carrer Remei, 15 - 08290 Cerdanyola del Vallés - Barcelona', '#44D0C1', '~/images/colles/castellerscerdanyola.png'),
('Castellers de Cornellà', false, 'Els Castellers de Cornellà ens presentem el 2 de Juny de 1991 per la nostra Festa Major. Tot i que en la pressentació fem uns tremolossos castells de 5, en els sis mesos que ens resten de temporada assolim tots els castells de sis, i ja fem les postres primeres temptatives de castells de set. Des d´aleshores fins ara, la colla manté el nivell com a colla de set, assolint el 2 de 7, com a màxim castell. La lluita per a fer castells de vuit, i l´augment de camises liles a la colla segueixen sent els nostres objectius.', '+34661868606', 'colla@castellerscornella.org', 'http://www.castellersdecornella.com', 'Passatge de la plàsmica s/n, 08940 Cornellà', '#9045DD', '~/images/colles/castellerscornella.png'),
('Castellers de Gavà', false, null, null, 'castellersdegava@gmail.com', null, 'Casal American Lake - Carrer Castelldefels, 1, Gavà', '#18249A', '~/images/colles/castellersgava.png'),
('Castellers de l’Alt Maresme', false, 'La idea de crear la colla de Castellers de l’Alt Maresme es va començar a gestar el 25 d’octubre de 2013, en una trobada informativa per tal de començar a donar vida a la criatura. L’objectiu principal de la colla és el de representar a les 6 poblacions que formen l’Alt Maresme (Calella, Pineda, Santa Susanna, Malgrat, Palafolls i Tordera), amb la intenció d’aglutinar el màxim de gent possible per tal d’arribar a esdevenir una colla gran amb el pas dels anys.', '+34690604269', 'castellersaltmaresme@gmail.com', 'http://www.castellersaltmaresme.cat', 'Carrer Verdaguer, 15, 08397 Pineda de Mar, Barcelona', '#FD0000', '~/images/colles/castellersaltmaresme.png'),
('Castellers de la Sagrada Família', false, 'La colla es va començar a gestar la tardor de l’any 2000 quan tres castellers actius en altres colles i residents al barri de la Sagrada Família es van possar en contacte amb la Fundació Claror i l’Associació de Veïns i Veïnes de la Sagrada Família per fer una crida a la gent del barri amb l’objectiu de veure la viabilitat de crear aquesta nova Colla. D’aquesta primera crida es van fer ressò un centenar de persones, i per aixó es va tirar endavant el projecte. L’entitat sería la sisena després dels Castellers de Barcelona, dels Castellers de Sants, dels de Vila de Gràcia, dels del Poble-Sec i dels de Sarrià (avui desapareguts).', '+34605045837', 'junta@castellerssagradafamilia.cat', 'http://www.castellerssagradafamilia.cat/', 'Passatge de Sant Pau, 16 08025, Barcelona', '#2A7E6B', '~/images/colles/castellerssagradafamilia.png'),
('Castellers de la Vila de Gràcia', false, 'Els castells a Gràcia no són una cosa nova, sinó que hi ha referències històriques que daten ja des de finals del segle XIX fins a principis dels anys trenta del segle XX. Cap el 1890 es va constituir una colla formada per gent procedent del camp de Tarragona que migraren en motiu de la crisi de la fil·loxera, els Xiquets de Gràcia. Possiblement, aquests nous graciencs van trobar, en l’ambient popular de la llavors encara Vila de Gràcia, l’atmosfera propícia per desenvolupar-hi l’activitat castellera en forma de colla local. ', '+34656981153', 'info@cvg.cat', 'cvg.cat', 'Can Musons C/ Alzina 7, Barcelona, 08024', '#004D9E', '~/images/colles/castellersviladegracia.png'),
('Castellers de les Roquetes', false, null, null, 'castellersdelesroquetes@gmail.com', 'http://www.castellersdelesroquetes.cat/', '08812 Les Roquetes del Garraf (Sant Pere de Ribes)', '#828385', '~/images/colles/castellersroquetes.png'),
('Castellers de Lleida', false, 'ls Castellers de Lleida vam néixer el 16 de març de 1995, gràcies a una iniciativa de la delegació de Cultura a Lleida amb la col·laboració de l’Organització de Veïns del barri de Pardinyes (ORVEPARD). Des de llavors aixequem castells amb la il·lusió d’anar cada dia més amunt. Ha calgut, cert, superar dificultats amb l’esforç de molta gent perquè avui la nostra ciutat pogués gaudir d’una gran colla castellera, amb el reconeixement de tot el món casteller. Però no hem de tenir-ne prou. La ciutat pot, vol i es mereix més. Lleida ha de consolidar una colla de vuit. El primer pas ja s’ha fet, tot assolint el dos de vuit amb folre, però no n’hi ha prou: cal consolidar-lo i per aquesta raó, més que mai, cal que entre tots siguem capaços de reunir un bon grapat de castellers més.', '+34607840043', 'correu@castellersdelleida.cat', 'http://www.castellersdelleida.cat', ' C/ Nou 1-17 25002 Lleida', '#660032', '~/images/colles/castellerslleida.png'),
('Castellers de Mallorca', false, null, '+34629306242', 'info@castellersdemallorca.com', 'http://www.castellersdemallorca.com', 'C/Bisbe Cabanelles, 25 (Escola Son Canals), Mallorca', '#9E4F63', '~/images/colles/castellersmallorca.png'),
('Castellers de Mollet', false, 'Castellers de Mollet és una colla castellera creada l’any 1992 per un grup d’aficionats de Mollet del Vallès amb l’objectiu de fer castells i de divulgar i promoure el fet casteller a la ciutat i la seva àrea d’influència. Legalment, la colla és una associació amb els estatuts aprovats per la Generalitat de Catalunya i inscrita al registre d’associacions de l’Ajuntament de Mollet del Vallès. El color de la nostra camisa és el verd clar.', '+34615384482', null, 'http://www.castellersdemollet.cat/', 'Centre Cívic L’Era, Carrer Cervantes, 19-23 08100 – Mollet del Vallès', '#95C59A', '~/images/colles/castellersmollet.png'),
('Castellers de Montcada i Reixac', false, 'A principis de l’estiu de 1991 van començar els assaigs per mirar de formar una colla castellera a Montcada. Val a dir que en aquella època, ni a Montcada ni al Vallès en general (tret dels Minyons de Terrassa), no hi havia cap tradició castellera. Al mes de juny i amb uns 40 membres aproximadament, van començar a fer camí els Castellers de Montcada i Reixac. La colla Castellera va rebre el suport de l’ABI, que la prengué com a secció de l’entitat.', '+34717702977 ', 'castellersdemontcadaireixac@gmail.com', 'http://www.castellersdemontcadaireixac.cat/', 'Carrer Colom, 5 Montcada i Reixac ', '#B22222', '~/images/colles/castellersmontcada.png'),
('Castellers de Rubí', false, 'La seva història  començà una nit en una xerrada d’un bar qualsevol de la vila, on es reunien uns joves aficionats a l’astronomia i uns quants de l’esplai (l’Eixam), i d’altres entitats de Cultura Popular i tradicional Rubinenca. Donat el moment creixent del món Casteller, se’ls acudí formar aquesta colla, anomenada  Els Castellers de Rubí,  tant mateix fou constituïda  el 20 d’abril de 1996, després d’unes quantes reunions prèvies al febrer del mateix any.', '+34654474588', 'castellersderubi@castellersderubi.cat', 'http://www.castellersderubi.cat', 'Carrer del Dr. Gimbernat, 4, 08191 Rubí, Barcelona', '#D04D67', '~/images/colles/castellersrubi.png'),
('Castellers de Sabadell', false, 'A principis de l’estiu de 1994, i sota l’empenta d’uns quants il·luminats, van començar els assaigs per mirar de formar una colla castellera a Sabadell. Val a dir que en aquella època, a Sabadell, no hi havia cap tradició castellera i només es veien castells per la Festa Major, sempre de la mà de colles foranes. Fou precisament en acabar l’aixecada de castells de la Festa Major del 1994 que, uns espontanis van alçar un pilar de 3 reivindicatiu que va aixecar força expectació i, de retruc, va donar a conèixer la encara no-nata colla local.', '+34672131312', 'saballuts@saballuts.org', 'http://colla.saballuts.org/', 'Carrer Emprius 13, Sabadell', '#006C57', '~/images/colles/saballuts.png'),
('Castellers de Sant Adrià', false, 'Som els Passadors i fem castells! El nostre sobrenom reivindica un dels oficis més antics de Sant Adrià de Besòs. Antigament les fortes riades, conegudes com a besosades, s’emportaven els ponts del nostre riu. Quan això passava, els Passadors traspassaven a coll-i-be als habitants del nostre poble.', '+34626130507', 'castellersdesantadria@gmail.com', 'https://passadors.wordpress.com/', 'Carretera de Mataró, 124, 08930 Sant Adrià de Besòs', '#771552', '~/images/colles/castellerssantadria.png'),
('Castellers de Sant Cugat', false, 'Els Castellers de Sant Cugat van néixer l’any 1996, i gràcies a una molt bona resposta dels santcugatencs, ara podem dir que estem plenament consolidats i que som una entitat més dins el teixit associatiu del nostre poble. Una entitat que aplega unes dues-centes persones amb un objectiu comú: fer dels castells una tradició arrelada al municipi.', '+34639963937', 'relacionspubliques@castellersdesantcugat.org', 'www.castellersdesantcugat.cat', 'Rambla Can Mora, 11 – local 2 08172 Sant Cugat del Vallés', '#295C3D', '~/images/colles/castellerssantcugat.png'),
('Castellers de Sant Feliu', false, 'Som una colla castellera que es va crear el mes de Gener de l’any 1996, per un grup d’amics del poble de Sant Feliu de Llobregat amb l’objectiu de forma una colla castellera i divulgar aquesta activista cultural, tan arrelada a Catalunya i altres indrets, al nostre poble. Som una colla castellera sense ànim de lucre on el que sí que es busca és l’esforç, el treball amb equip, l’afany de superació, el compromís, la il·lusió, les ganes i l’esperit de lluita per fer castells.', '+34633600059', 'castellers.santfeliu@gmail.com', 'http://castellersdesantfeliu.cat', 'C. Vidal i Ribas, 23-25, 08980, Sant Feliu de Llobregat', '#FF8989', '~/images/colles/castellerssantfeliu.png'),
('Castellers de Sant Pere i Sant Pau', false, 'Son poques les activitats que dones i homes poden realitzar plegats i que aglutinin tantes sensacions, treball en equip, afany de superació i esperit de solidaritat com es el FER CASTELLS, una tradició del nostre país la qual es un fet identitari i motiu d’orgull. La Colla Castellera de Sant Pere i Sant Pau forma part d’aquest col·lectiu, nascuda al 1990 convivint amb les altres colles de la ciutat i guanyant-se el respecte del mon casteller.', '+34977200078', ' info@ccspisp.cat ', 'http://www.ccspisp.cat/', 'A s/n 43007 Sant Pere i Sant Pau (Tarragona)', '#0C6343', '~/images/colles/castellerssantpereisantpau.png'),
('Castellers de Sant Vicenç dels Horts', false, 'Durant el mes de juliol de 2012 diferents vicentins es van posar en contacte per crear una colla al poble i durant els següents mesos es van realitzar fins a quatre reunions per tal de parlar de la seva formació. El 13 d’octubre de 2012 es va realitzar l’assemblea on es va constituir la colla i el 9 de novembre de 2012 es va fer la seva presentació pública. El dia 16 de novembre de 2012 es va convocar el primer assaig casteller, on hi van assistir una trentena de persones.', null, 'castellers@carallots.cat', 'http://www.carallots.cat', 'Carrer del Mestre Ramón Camps, 15 08620, Sant Vicenç dels Horts', '#FF7F00', '~/images/colles/carallots.png'),
('Castellers de Santa Coloma', false, 'Associació Colla Castellera de Santa Coloma és una entitat cultural que va neixer el 1985. A la seva dilatada trajectòria ha tingut alguns alts i baixos però sempre s’ha mantingut ferma als principis castellers d’agrupar la seva colla amb un vincle de germanor que ampliem amb les altres colles castelleres.', '+34687402371', 'castellersdesantacoloma@hotmail.com', 'http://castellersdesantac.wixsite.com', 'CPTC (Centre popular de tradicions catalanes) Joan Peiró, Plaça Manent nº1, 08921 Santa Coloma de Gramanet', '#14B3E2', '~/images/colles/castellerssantacoloma.png'),
('Castellers de Santpedor', false, 'Tot va començar a principis de març del 2013, quan l’associació de penyes del poble de Santpedor va fer una gran proposta a totes les penyes del poble. Es tractava de fer un castell amb integrants de totes les penyes, en motiu de la festa major. Proposta un xic arriscada, sí, però tothom s’ho va agafar com un repte personal. El castell (un 3 de 6) es va assajar a consciencia i es va aconseguir carregar i descarregar. Un cop passat el gran dia a tots ens va quedar un bon regust de boca i un bon grup de jovent va voler seguir, reunint-se per tal de continuar gaudint de la pràctica castellera. El final del inici del nostre camí, va arribar amb la constitució de la Colla Castellera “Castellers de Santpedor”, i va culminar el dia 14 de juny de 2014, amb el seu Bateig oficial. Per aquesta actuació es va comptar amb dues colles “Padrines”, que van ser els Sagals d’Osona i els Salats de Súria. Després d’un període com a colla en formació, el febrer de l’any 2015 la Coordinadora de Colles Castelleres de Catalunya determina que els Castellers de Santpedor ja són colla castellera de ple dret.', '+34619169392', 'castellsasantpedor@gmail.com', 'http://castellersdesantpedor.cat', 'Plaça Gran, 6, 08251 Santpedor, Barcelona', '#FED136', '~/images/colles/castellerssantpedor.png'),
('Castellers de Sants', false, 'Després del concurs de Tarragona de 1990, un grup de persones del barri de Sants, es plantegen la possibilitat d’iniciar una colla castellera. Un cop establerts els primers contactes i havent informat al barri de les intencions, els assajos comencen a principis del mes d’Octubre de 1992 al Casinet d’Hostafrancs. Durant els primers mesos, castellers de les colles de Barcelona i Cornellà ens ajuden a adquirir les primeres nocions sobre tècnica castellera. Cap al Febrer de l’any 1993 la colla comença a funcionar per sí sola i escollim el primer Cap de Colla, Oriol Quin, que juntament amb tot l’equip tècnic assumeixen la responsabilitat de tirar la colla endavant. Paral·lelament iniciem els assajos del grup de grallers i timbalers i editem el primer exemplar de la revista de la colla. Els assajos continuen i el dia 9 de Maig ens presentem oficialment a la plaça Bonet i Muixí, apadrinats per les colles de Barcelona i de Cornellà, i estrenant la nostra camisa grisa. Aquell dia descarreguem el pilar de quatre caminat (entrada a plaça), el quatre de sis, el tres de sis i el cinc de cinc net. Arriba l’estiu i, el 8 d’Agost, en l’actuació per presentar el monument casteller de F. Anglès, carreguem el primer pilar de cinc. El Setembre següent, el dia 10 concretament, carreguem la primera torre de sis a Hostafrancs i el 23, al local dels Minyons de Terrassa, descarreguem la torre de sis i el pilar de cinc. Acabem l’any descarregant tots els castells de la gamma de sis (excepte el tres per sota): quatre de sis, tres de sis, quatre amb el pilar, cinc de sis, torre de sis i el pilar de cinc. El dia 8 de Desembre, en la darrera actuació de l’any, carreguem un incipient quatre de set a Castelldefels. En aquest primer any de vida hem aconseguit èxits prou notables, ja que apart de fer els nostres primers passos com a colla, hem aconseguit despertar el gust pels castells en un barri amb cap tradició castellera i hem donat una molt bona impressió a totes les places on hem actuat.', '+34609442495', 'borinots@borinots.cat', 'http://www.borinots.cat', 'Carrer dels Comtes de Bell-Lloc, 86, 08014 Barcelona', '#D2DADD', '~/images/colles/borinots.png'),
('Castellers de Solsona', false, null, '+34630991375', 'info@castellersdesolsona.cat', 'http://www.castellersdesolsona.cat/', 'Av. Sant Jordi, 25 Solsona' , '#FFFF3E', '~/images/colles/castellerssolsona.png'),
('Castellers de Terrassa', false, 'Els Castellers de Terrassa són una de les primeres colles que sorgeixen fora de les comarques tradicionalment castelleres, en la línia de l’expansió que ha experimentat el món casteller els darrers vint-i-cinc anys. El 1980 va ser fundada la colla quan un grup de persones encapçalades pel recordat casteller Emili Miró van decidir fundar una nova colla castellera a la ciutat, que es presentà oficialment per la Festa Major de Terrassa d’aquell any, ja amb la camisa blau turquesa que caracteritza la colla. En els tres primers anys d’existència, la colla va aconseguir la fita de descarregar la quasi totalitat de la gamma dels castells de set: quatre de set, tres de set, cinc de set, tres de set aixecat per sota i quatre de set amb l’agulla. A més, cal fer un esment especial a un castell tan singular com el pilar de cinc sense mans del segon al terç (1981), que cap colla havia fet des de l’any 1933. Després d’una època d’alts i baixos pel que fa els castells, la colla començà una important progressió fins aconseguir de descarregar el primer castell de vuit.', '+34937890165', 'info@castellersdeterrassa.cat', 'http://www.castellersdeterrassa.cat/', 'Carrer de Pasteur, 48, 08224 Terrassa', '#0CBAFD', '~/images/colles/castellersterrassa.png'),
('Castellers de Viladecans', false, 'Els Castellers de Viladecans som la colla castellera de la ciutat de Viladecans (Baix Llobregat), fundada l’any 2012. El nostre color de camisa és el verd clar, i els nostres màxims castells són el 2de6, el 5de6, el 4de6a, i el 3de6a. Som una entitat cultural que té com a objectiu la promoció i divulgació del fet casteller mitjançant actuacions i diades castellers amb altres colles, i altres actes, com tallers o assajos públics, per Viladecans i rodalies. Som un grup de viladecanencs i viladecanenques i de ciutats properes, entusiasmats per fer castells i per fer més gran a colla castellera de la nostra ciutat.', '+34665029313', 'castellersviladecans@gmail.com', 'http://castellersdeviladecans.blogspot.com.es', 'Escola Enxaneta. Carrer Remolar, s/n. 08840 - Viladecans', '#B0E32E', '~/images/colles/castellersviladecans.png'),
('Castellers de Vilafranca', false, 'Els Castellers de Vilafranca són una institució cultural i esportiva catalana que té com a principal objectiu fer castells. Són una organització sense ànim de lucre i tenen la consideració d’entitat d’interès públic. La Colla es va fundar el 1948, fruit de la gran afició, tradició i arrelament del fet casteller a Vilafranca del Penedès, que es remunta als temps del Ball de Valencians (s. XVIII) precursor dels castells actuals. Actualment formen part de la Colla més de quatre-cents castellers i castelleres, de totes les edats, de forma lliure i voluntària sense cap tipus de distinció social, política, cultural o religiosa. A tots els uneix la mateixa afició d’aixecar castells, els valors democràtics, la cooperació i el treball en equip, l’esperit de superació constant, i continuar estant al capdamunt del món casteller. La seu social dels Castellers de Vilafranca és Cal Figarot, Casa Via Raventós, adquirida l’any 1983.', '+34681027380', 'info@castellersdevilafranca.cat', 'http://app.castellersdevilafranca.com/', 'Cal Figarot, Carrer del Genereal Prim, 11 08720 Vilafranca del Penedès', '#00B09A', '~/images/colles/castellersvilafranca.png'),
('Castellers del Poble Sec', false, null, '+34653113496', 'cps@castellersdelpoblesec.cat', 'http://castellersdelpoblesec.cat', 'C/Blesa, 7 | 08004 | Barcelona', '#99CCFF', '~/images/colles/castellerspoblesec.png'),
('Castellers del Prat de Llobregat', false, 'Els Castellers del Prat de Llobregat som la colla castellera de la ciutat del Prat, oberta a la participació de totes les persones interessades en la cultura popular i en l’activitat de fer castells, independentment del seu origen, raça, religió, orientació sexual o llengua.', '+34666336482', 'castellersdelprat@gmail.com', 'http://www.castellersdelprat.cat', 'Escola Joan Maragall C. Joan Maragall, 40 08820 El Prat de Llobregat', '#0F75BD', '~/images/colles/castellersdelprat.png'),
('Castellers del Riberal', false, 'Aire Nou de Bao neix el 12 d’octubre 1995. Les seves finalitats : protegir i fer conèixer el patrimoni cultural de Bao i del Riberal. Ràpidament l’objectiu es converteix en el de promoure i defensar la llengua i la cultura catalanes. L’entitat proposa cursos de català per a adults, iniciació a la sardana i iniciació a la llengua catalana per a la mainada sota forma de teatre i cançons.', '+33609812387 ', 'malogil@gmail.com', 'http://www.airenou.cat', 'La Vilbau, Bao, Catalunya Nord 66540', '#00FF7F', '~/images/colles/castellersriberal.png'),
('Colla Castellera de Figueres', false, 'A la Colla Castellera de Figueres fem castells humans, una activitat lúdic cultural on tothom pot participar, des del membre més petit de la família fins al més gran, tothom hi té un lloc. És una activitat social, integradora, totalment gratuïta i molt divertida. A més de castells, també fem altres activitats com festes, sopars, esports (campionats de futbito, etc.) excursions, anem a esquiar… i moltes coses més.', '+34615058958', 'info@figueres.cc', 'http://www.figueres.cc/', 'Centre cultural Molí de l’Anguila, Carrer Via Làctea s/n 17600, Figueres', '#5B008C', '~/images/colles/collacastellerafigueres.png'),
('Colla Castellera de la Gavarresa', false, 'Els Picapolls de la Gavarresa som una colla castellera de nom vinícola i cognom fluvial. Actualment en procés de formació, els Picapolls intentem abraçar tots els pobles de la Vall de la Gavarresa –amb localització a la comarca del Bages i d’on prové la varietat de raïm Picapoll– per de mica en mica anar guanyant amplada i alçada.', '+34666767657 ', 'picapollsgavarresa@gmail.com', 'https://picapollsgavarresa.wordpress.com', 'Pg. Diagonal s/n (Kruïlla d’entitats), Artés (La Gavarresa)', '#A5649D', '~/images/colles/collacastelleragavarressa.png'),
('Xoriguers de la UDG', true, null, '+34675062985', 'xoriguers.udg@gmail.com', 'http://www.xoriguers.org/', 'Av. Lluís Santaló sn. Edifici P-II, Girona ', '#689AC2', '~/images/colles/xoriguers.png'),
('Trempats de la UPF', true, null, null, 'trempatsUPF@gmail.com', null, null, '#9F6E4F', '~/images/colles/trempats.png'),
('Penjats del Campus de Manresa', true, 'La colla castellera Penjats del Campus de Manresa va néixer el 2013 fruit d’una iniciativa dels estudiants de la Escola Politècnica Superior d’Enginyeria de Manresa (EPSEM-UPC), de la Fundació Universitària del Bages (FUB) i de l’Institut Lacetània. Actualment està formada per unes 40 persones que volen continuar creixent dintre del món universitari de la ciutat.', '+34660903961', 'penjatsdelcampus@gmail.com', 'http://www.campusmanresa.net/menu/contingut/titol/penjats-del-campus', null, '#FF0000', '~/images/colles/penjats.png'),
('Pataquers de la URV', true, null, null, 'pataquers@urv.cat', null, 'c/Angel Guimerà 35 2 A. Reus', '#FF7F00', '~/images/colles/pataquers.png'),
('Passerells del TCM', true, null, null, 'collacastelleratcm@gmail.com', 'https://passerellstcm.wordpress.com/', null, '#CD9DD2', '~/images/colles/passerells.png'),
('Llunàtics UPC Vilanova', true, 'Els Llunàtics, som una colla que es va començar a gestar al mes de Novembre del 2012, on els 4 membres fundadors, els quals eren membres de diferents colles convencionals de castells, es van trobar per donar el primer pas en la creació de la colla. El motiu principal pel qual es va tirar endavant aquesta iniciativa, va ser que els 4 membres fundadors volien fer castells universitaris però era impossible desplaçar-se fins Barcelona o Tarragona. Així doncs, van tenir la idea de crear una colla castellera universitària a Vilanova i la Geltrú, amb seu l’Escola Politècnica Superior d’Enginyeria de Vilanova i la Geltrú.', '+34938967701', 'llunaticsupcvilanova@gmail.com', 'https://llunaticsupcvilanova.wordpress.com/', 'Av. Víctor Balaguer 1, 08800 Vilanova i la Geltrú', '#014CA9', '~/images/colles/llunatics.png'),
('Grillats del Campus del Baix Llobregat', true, null, null, 'grillatscbl@gmail.com', null, null, '#930744', '~/images/colles/grillats.png'),
('Ganàpies de la UAB', true, null, '+34608511909', 'junta@ganapies.cat', 'http://ganapies.cat/', 'Plaça Cívica, Edifici Estudiants, R-112 Universitat Autònoma de Barcelona', '#5A8AA2', '~/images/colles/ganapies.png'),
('Engrescats de URL', true, null, null, 'engrescatsurl@gmail.com', 'http://engrescatsurl.esy.es/', null, '#FEDE34', '~/images/colles/engrescats.png'),
('Emboirats de la Universitat de Vic', true, 'Som estudiants de la Universitat de Vic-Universitat Central de Catalunya provinents de diferents estudis, edats, localitats… Però tots tenim ganes d’aixecar els Emboirats!', '+34679048358', 'emboirats@uvic.cat', 'https://www.uvic.cat/es/node/2631', null, '#DC244B', '~/images/colles/emboirats.png'),
('Bergants del Campus de Terrassa', true, null,'+34659166323', 'bergants.ct@gmail.com', null, 'C/ Colom, 11 Terrassa', '#B8DA48', '~/images/colles/bergantsterrassa.png'),
('Arreplegats de la Zona Universitària' , true, 'Som una colla castellera universitària amb més de 20 anys a les espatlles. D”aquí i d”allà, acostem el món dels castells als estudiants. Però som més que castells universitaris. Som una colla d’amics que sempre estaran al teu costat. Que no t’ho expliquin, forma’n part!', '+34650115558', 'arreplegats@arreplegats.cat', 'http://www.arreplegats.cat/', 'Avinguda Diagonal, 696 Barcelona 08028', '#1FBBA6', '~/images/colles/arreplegatszonauniversitaria.png'),
('Xiquets del Serrallo' , false, null, '+34977221644', 'info@xiquetsdelserrallo.cat', 'http://www.xiquetsdelserrallo.cat/', 'Carrer Nou de Santa Tecla, 9 43004 – Tarragona', '#006AC6', '~/images/colles/xiquetsserrallo.png'),
('Xiquets de Tarragona' , false, null, '+34977239699', 'info@xiquetsdetarragona.cat', 'http://xiquetsdetarragona.cat/', 'C/Santa Anna, 1, 43003 Tarragona', '#BE1818', '~/images/colles/xiquetstarragona.png'),
('Xiquets de Vila-seca' , false, 'El 17 de gener de 1991 sortien a plaça per primer cop els Xiquets de Vila-seca, una colla castellera de nova fornada que seguí actuant fins a la seva dissolució l’any 1997.', '+34648294737', 'cc.xiquetsdevilaseca@gmail.com', 'http://www.xiquetsdevila-seca.cat/', 'C/ De la Font, 12 Vila-seca, 43480', '#2B6CA3', '~/images/colles/xiquetsvilaseca.png'),
('Xiquets de Cambrils' , false, 'La Colla Castellera Xiquets de Cambrils es va iniciar l’any 1993, realitzant assajos a la plaça de l’Església de Santa Maria, i la primera actuació la van realitzar a la mateixa plaça el 8 de desembre del mateix any. En la primera actuació es van realitzar castells de 6 i l’any següent es descarregava el primer 4 de 7. En els anys d’activitat es van descarregar castells com el pilar de cinc, el 2 de 6, el 4 de 7, el 3 de 7, el 4 de 7 amb el pilar i el 3 de 7 amb el pilar. Els Xiquets de Cambrils van mantenir l’activitat entre el 1993 i el 2001, i van actuar a Reus en diverses ocasions, a Madrid, a Beseit, a Barcelona, a l’Hospitalet o a les Terres de l’Ebre i també van participar al concurs de Castells de Torredembarra, on van guanyar el premi als millors grallers i al millor 3 de 7. El 8 de desembre de 1994 es va penjar una placa commemorativa a la façana de l’Església de Santa Maria del primer pilar de 4 aixecat pels Xiquets de Cambrils el 1993. Posteriorment amb les obres de la façana es va fer malbé i es va retirar. El dia 8 de desembre 2013, diada de Festa Major de la Immaculada, coincidint amb els 20 anys de la primera actuació a la plaça i amb la recuperació de nou de la Colla, es va restituir la placa commemorativa i es va realitzar un pilar de 4. Vestim camisa de color burdeus.', null, 'xiquetsdecambrils@hotmail.com', 'https://xiquetsdecambrils.cat', 'Carrer de Sant Plàcid 18-20, Cambrils, 43850', '#CF3841', '~/images/colles/xiquetscambrils.png'),
('Xiquets de Reus' , false, 'Els Xiquets de Reus és la colla castellera de la ciutat de Reus. Es va fundar formalment el 18 de novembre de 1981, pel 125è aniversari del ferrocarril a Reus. La seva camisa és marró com l’avellana i el pantaló blanc. El 19 de setembre del 2006 la colla va ser distingida amb la Creu de Sant Jordi, en reconeixement a la tasca de difusió del fet casteller en el seu 25è aniversari.', '+34977344070', 'xdr@xiquetsdereus.cat', 'http://www.xiquetsdereus.cat/', 'Carrer de Rosich, 4 Reus', '#A65F12', '~/images/colles/xiquetsreus.png'),
('Xiquets d’Alcover' , false, ' Alcover té un llarg recorregut històric al món casteller. Forma part de les poblacions amb tradició castellera més antiga de l’anomenada “zona tradicional”. Segons va escriure el doctor Francesc Ballester l’any 1971, un alcoverenc de renom Cargol juntament amb quatre o cinc alcoverencs més, van fer el pilar de tres l’any 1789, i l’any següent un pilar de quatre.', '+34653176460', 'xiquets@xiquetsdalcover.cat', 'http://www.xiquetsdalcover.cat/', 'C/ Hort de Sant Antoni, 1 (Mateix edifici que l’EMMA i Alcover Radio) - Alcover', '#C6E7B7', '~/images/colles/xiquetsalcover.png'),
('Xiqüelos i Xiqüeles del Delta' , false, 'Per trobar l’origen de la colla, cal traslladar-nos fins a Barcelona, on 6 companys d’Amposta i 1 de Deltebre, integrants de la colla  castellera de la Sagrada Família, van encoratjar-se a impulsar i  portar per primer cop la tradició castellera a les Terres de l’Ebre. Això passava a finals del 2012, però paral·lelament succeïa el mateix a l’Ametlla de Mar, ciutat de Los Xics Caleros. Així, los xiqüelos i xiqüeles, també coneguts pel nostre “Blau Delta” de la camisa, passaríem a ser la segona colla castellera de les Terres de l’Ebre i la primera del Delta.', null, 'xiquelosixiquelesdeldelta@gmail.com', 'http://xiquelosixiquelesdeldelta.cat/', 'Carrer de l’Alba, 2, La Ràpita (Entrada de darrere del Mercat)', '#2F7CBF', '~/images/colles/xiqueles.png'),
('Xics de Granollers', false, 'Des de 1991 Els Xics de Granollers ha arrelat els castells a la ciutat de Granollers i al Vallès Oriental on l’experiència amb el món casteller era pràcticament nul.la.', '+34690058095', 'colla@xics.org', 'http://www.xics.cat/', 'C/ Enric Prat de la Riba 77, 08401 Granollers', '#9A0000', '~/images/colles/xicsgranollers.png'),
('Xics Caleros' , false, 'Los Xics Caleros som una entitat cultural esportiva catalana que té com a objectiu principal fer castells. Som una organització sense ànim de lucre i la Primera Colla Castellera que s’ha constituït a les Terres de l’Ebre.', null, 'xicscaleros@gmail.com', 'http://www.losxicscaleros.cat/', null, '#B6C2D2', '~/images/colles/xicscaleros.png'),
('Xicots de Vilafranca' , false, null, '+34630039295', 'xicots@xicotsvilafranca.com', 'www.xicotsdevilafranca.com', 'Plaça de Milà i Fontanals, 3 Vilafranca del Penedès', '#D5111A', '~/images/colles/xicotsvilafranca.png'),
('Xerrics d’Olot' , false, null, '+34650177701', 'xerrics@xerrics.cat', 'http://www.xerrics.cat/', 'Xerrics d’Olot Ap. de Correus 144 17800 - Olot', '#882222', '~/images/colles/xerrics.png'),
('Vailets de l’Empordà' , false, 'Des de l’any 2009 un grup d’amics van començar a donar voltes a la idea de formar una colla a l’Empordà. No és fins a l’octubre del 2012 que la idea agafa forma amb l’aparició de més gent amb la mateixa voluntat i es planteja la opció d’engegar aquest nou projecte. Tres dels amics van reunir-se amb l’Ajuntament de Castelló d’Empúries, ja que és un municipi ben situat a la comarca, amb un teixit social actiu i amb equipaments municipals per a desenvolupar-hi l’activitat castellera. La resposta del consistori fou positiva, cedint el local d’assaig sense cap cost. És llavors quan aquest grup de gent decideix crear l’entitat formalment i ser una associació censada al municipi de Castelló.', null, 'vailetsdelemporda@gmail.com', 'https://sites.google.com/site/vailetsdelemporda/', null, '#C0E3F4', '~/images/colles/vailetsemporad.png'),
('Vailets de Gelida' , false, 'La colla dels Vailets de Gelida neix a la tardor de 1985 i els primers assaigs es fan el novembre del mateix any. Aquests assaigs foren monitoritzats, en un principi, per algun dels membres dels Minyons de Terrassa, els quals anaven a Gelida a ensenyar-los a fer castells. Per la Festa Major de Gelida, el 23 d’agost del 1986, van fer els primers castells: un 4 de 6, un 2 de 5, un 3 de 6 carregat al segon intent i un pilar de 4. L’actuació es realitzà al costat de les colles dels Minyons de Terrassa, que apadrinà la nova colla, i els Castellers de Barcelona. A finals del 1986 es proposa a l’Ajuntament la cessió de l’antic escorxador com a local social per als Vailets, el qual els hi cedeix. Així, durant l’hivern del 1986 al 1987 treballaren en la rehabilitació del local.', '+34627276683', 'vailetsdegelida@gmail.com', 'http://vailetsdegelida.blogspot.com.es/', 'CIC (Centre d’Iniciatives Culturals) C/ Mossèn Jaume Via 17, 08790 - Gelida', '#32CD32', '~/images/colla/vailetsgelida.png'),
('Torraires de Montblanc', false, null, '+34675580280', 'info@torrairesdemontblanc.cat', 'http://usuaris.tinet.cat/torraire/', 'Carrer de la Plebania, 10 (darrera de l’església de Santa Maria) 43400 Montblanc', '#790000', '~/images/colles/torraires.png'),
('Salats de Súria', false, 'La Colla Castellera Salats de Súria neix l’any 2008 per iniciativa de joves suriencs i es va presentar el dissabte sant de 2008, quan diversos membres de les colles caramellaires de Súria es van unir per aixecar els primers castells. Tant la camisa de color potassa (salmó) com el nom de la colla fan referència als jaciments de sal potàssica de la zona.', '+34619063652', 'salatsdesuria@hotmail.com', 'http://salats.blogspot.com.es/', 'Polígon industrial Abadal (nau pumatex B)', '#F5AA94', '~/images/colles/salats.png'),
('Sagals d’Osona', false, null, '+34609454779', 'secretaria@sagals.cat', 'http://sagals.cat/', 'Plaça Vicens Albert Ballester, Edifici La Central Vic ', '#FF8000', '~/images/colles/sagals.png'),
('Pallagos del Conflent' , false, 'Nascuts a la primavera del 2012 els Pallagos del Conflent donen vida a bona part del Conflent. El seu bateig com a colla oficial es va fer el 12 de maig del 2013 a Catllar. Dues vegades per setmana, la colla es reuneix als pobles de Vinça i Prada del Conflent per assajar. Actualment els Pallagos compten amb unes 120 persones de totes les edats i constitucions. Tothom hi pot trobar el seu lloc ja sigui a la pinya , al tronc, al pom de d’alt pels més petits i fins i tot amb els musics que acompanyen els castells durant les actuacions, composats de gralles i timbals.', '+33643718108', 'pallagosdelconflent@gmail.com', 'http://pallagos.com/', 'Sala dels Pallagos (al costat de l’estació de tren) 66500 Prada', '#DF0101' , '~/images/colles/pallagos.png'),
('Nois de la Torre' , false, null, '+34622411975', 'noisdelatorre@hotmail.com', 'http://www.noisdelatorre.cat', 'Carrer Carnisseria, 16', '#7AA9EA', '~/images/colles/noistorre.png'),
('Nens del Vendrell' , false, 'L’any 1926 un grup de vendrellencs s’engresquen a crear una colla castellera, animats en veure que es va recuperant aquesta tradició perduda a Tarragona. S’estrenen per la Fira del mateix any, i es converteixen en un de les primeres colles castelleres constituïdes com a associació. En aquell moment i durant uns quants anys existirien al Vendrell fins a 3 colles castelleres: els Nens, els Caneles i els Mirons. L’any 1935 arriba el primer 4d8 carregat, que es podrà descarregar l’any 1946. El 15 d’octubre del 1951 es descarrega el primer 3d8 del segle xx, que coincideix amb el 25è aniversari de la Colla.', '+34654168769', 'info@nensdelvendrell.cat', 'http://www.nensdelvendrell.cat/', 'Av. Riera de la Bisbal 22, 43700 El Vendrell', '#C73122', '~/images/colles/nensvendrell.png'),
('Moixiganguers d’Igualada' , false, 'Els Moixiganguers som la colla castellera de la ciutat d’Igualada. Des de 1995, més de 600 persones han vestit ja la camisa morada que ens caracteritza.', '+34693714659', 'moixiganguers@moixiganguers.cat', 'http://moixiganguers.cat', 'Carrer Sant Antoni de Baix 106-108, 08700 Igualada', '#A63E97', '~/images/colles/moixinganguers.png'),
('Minyons de Terrassa' , false, 'Els Minyons de Terrassa vàrem fer la nostra presentació pública el dia 14 de juliol del 1979. Malgrat que a la nostra ciutat no hi havia una tradició castellera la colla va experimentar un important i ràpid progrés tècnic que ens ha portat a ocupar una posició capdavantera entre les més de seixanta agrupacions que actualment existeixen.', '+34937842856', 'minyons@minyons.cat', 'http://www.minyons.cat', 'Carrer Teatre 4-6, 08221 - Terrassa', '#BB9FC7', '~/images/colles/minyonsterrassa.png'),
('Minyons de l’Arboç' , false, 'Els Minyons de l’Arboç és una colla castellera de la població de l’Arboç, a la comarca del Baix Penedès, refundada el 2002. És l’hereva de la colla fundada el 1958 i desapareguda el 1985.', '+34634869872', 'colla@minyonsdelarboc.cat', 'http://www.minyonsdelarboc.cat', 'Apartat de correus 78, 43720 - L’Arboç Baix Penedès', '#D80F21', '~/images/colles/minyonsarbos.png'),
('Minyons de Santa Cristina d’Aro' , false, null, '+34649383121', 'minyonsdesantacristina@gmail.com', 'http://www.minyonsdesantacristina.cat/', 'Ctra. de Girona a Sant Feliu, km 28', '#86C440', '~/images/colles/minyonssantacristina.png'),
('Matossers de Molins de Rei' , false, null, '+34669500163', 'matossers@matossers.cat', 'http://www.matossers.cat/', 'Jacint Verdaguer, 48 (Federació Obrera) Molins de Rei', '#663B30', '~/images/colles/matossers.png'),
('Marrecs de Salt' , false, 'Els Marrecs de Salt som la més antiga de les nou colles que hi ha actualment en actiu a la província de Girona. Vam fundar la colla el 8 de novembre de 1995 a Salt i des de llavors portem la camisa de color blau Ter. El nostre millor castell és el 5 de 8.', '+34686476222', 'marrecs@gmail.com', 'http://marrecs.cat/', 'Carrer de Sant Antoni, 1, 17190 Salt, Girona', '#255686', '~/images/colles/marrecsalt.png'),
('Margeners de Guissona' , false, 'La idea de crear una colla castellera a la Segarra va néixer d’un grup d’amics de Guissona i rodalies que tenien ganes de fer alguna cosa en comú per tal de dinamitzar la vida associativa de la vila. Entre tots creien que fora bo que existís una entitat que incentivés l’activació cultural i la participació popular, cosa que podia ser possible a través de la creació d’una colla castellera. Els primers passos decidits es realitzaren el març de 2007 quan els mateixos joves van tirar endavant la iniciativa, gràcies també a la col·laboració de diversos castellers que havien fixat la seva residència a prop de Guissona.', '+34669945303', 'margeners@margeners.cat', 'https://margeners.cat/', 'Plaça Bisbe Benlloch, 2 25210 Guissona (Lleida)', '#794A2E', '~/images/colles/margenetsguissona.png'),
('Laietans de Gramenet' , false, 'Els Laietans de Gramenet som una colla castellera de la ciutat de Santa Coloma de Gramenet. La Colla va iniciar aquesta aventura el 28 de setembre de 2013 amb una motivació molt clara, fer ciutat fent castells. Veiem els “Castells” com una manera molt completa de fer societat i grup, una manera d’unir persones diferents per a aconseguir una fita comuna.', '+34648049169 ', 'laietansdegramenet@gmail.com', 'http://laietansdegramenet.wixsite.com/laietans', 'Centre Cívic del Fondo. Carrer Wagner, 19. 08923 Santa Coloma de Gramanet, Barcelona', '#4FAD30', '~/images/colles/laietans.png'),
('Esperxats de l’Estany' , false, 'La Colla Castellera Esperxats de l’Estany vam iniciar els nostres passos un 16 de Març del 2012 amb un taller obert a tothom que s’impartia al Centre Cívic de Porqueres. Aquest taller es va originar arran de la inquietud de dues persones que havien descobert el món dels castells a les seves respectives universitats i que volien transmetre-ho al seu entorn, el taller es convertí, durant l’estiu del 2012, en una entitat cultural de ple dret que ja agrupava una trentena de persones. La primera actuació destacable va ser per les festes de St.Martirià de Banyoles on es va poder comprovar la viabilitat del projecte i les ganes i motivacions dels seus membres, tot i estar en una zona de poca tradició castellera. El nostre bateig va ser el dissabte 18 de Maig del 2013 i va ser la primera vegada que ens vam posar la nostra camisa blau-verda, color de l’estany. Els nostres padrins van ser els Marrecs de Salt i la Colla Castellera de Figueres.', '+34628513223', 'ccesperxats@gmail.com', 'https://ccesperxats.wordpress.com', 'C/Barcelona, 93 - Banyoles', '#479B9A', '~/images/colles/esperxatsestany.png'),
('Encantats de Begues', false, 'Feia temps que en Josep Lluis i en Vicenç teníem ganes de crear una colla castellera a Begues, però hi havia dos inconvenients destacables, no hi ha tradició castellera i som un poble molt petit (uns 6.000 habitants). El 12-Maig-2012 vàrem convocar al poble de Begues per copsar si hi havia ganes de fer castells amb un audiovisual del tema i amb membres de colles castelleres que ens donaven suport, i l’èxit va ser gran , ens vam aplegar més de 100 persones amb il·lusió i ganes de fer castells (això en un poble com el nostre és molt !!) No teníem local i assajàvem davant de l’Església i això feia que més gent ens conegués i s’apuntés a la colla, que vàrem anomena "Els Encantats" doncs aquest és el malnom històric que tenim els d’aquesta vila, (gràcies Laura per aquesta proposta). Com he dit no hi ha tradició castellera, comencem de zero, però no ens hem rendit i hem cercat suport en colles properes ,com és el cas de Castelldefels , amb els quals hi tenim molta relació i ens ajudem mútuament, ells ens donen suport tècnic i nosaltres aportem gent per les seves actuacions. També comptem amb el suport de la colla de Barcelona amb els que hi ha molt bona relació El nostre primer castell (p4) va ser l’11-Setembre i l’èxit va ser tant gran que vàrem ser portada el següent butlletí municipal.', '+34665901984', 'elsencantats.begues@gmail.com', 'http://elsencantats.blogspot.com.es', 'Carrer Major 14, 08859 Begues Barcelona', '#117769', '~/images/colles/encantats.png'),
('Colla Vella dels Xiquets de Valls' , false, 'Els Castells, amb un resultat visual certament espectacular, van aparellats a tot tipus de vivències i a tot tipus de sensacions, i es sustenten bàsicament en valors com l’esforç, la preparació, la tenacitat, l’audàcia, la superació, la solidaritat, i per què no....la rivalitat algunes vegades. I tot amb un alt esperit d’equip i amb el segell de l’esforç compartit. Per totes aquestes raons, entre altres, els Castells formen part de les vides de moltes persones que, tant a Valls com a fora de Valls, són o se senten “Xiquets de Valls”.', '+34654396201', 'colla@collavella.cat', 'http://www.collavella.cat', 'Carretera de Tarragona, 2 | Apt. Correus nº73 - 43800 Valls (Tarragona)', '#FE9D96', '~/images/colles/vellaxiquetsvalls.png'),
('Colla Joves Xiquets de Valls' , false, 'Som cultura popular, som tradició, som festa i som l’origen dels castells. La Colla Joves Xiquets de Valls és colla castellera des de 1813, amb una història plena d’èxits, esforç, rauxa i vides compartides. Els castells són per a nosaltres una forma de viure, un llegat que avança, sempre integrador i obert. Colla capdavantera a les places, som molt més que castells i compartim activitats de tota mena. Castellers, Xiquets de Valls, amb camisa vermella i pantalons blancs, els colors de la ciutat bressol dels castells i també els colors de la passió i l’esperit de lluita, uns valors que ens defineixen com a colla.', '+34678743387', 'collajoves@collajoves.org', 'http://collajoves.com/', 'Carrer d’en Gassó,20, apartat de correus 96-43800 Valls (Tarragona)', '#B80E1E', '~/images/colles/jovesxiquetsvalls.png'),
('Colla Jove Xiquets de Vilafranca' , false, 'L’objectiu de la JOVE de Vilafranca és ser una colla moderna, respectar la tradició, dotar a la ciutat de Vilafranca d’un potencial casteller alternatiu i difondre el valors del fet casteller que han permès que els castells siguin reconeguts per la UNESCO com a Patrimoni Immaterial de la Humanitat. La plaça de Vilafranca és un referent casteller en quan a prestigi i historia, i la JOVE de Vilafranca vol formar-ne part. Sabem que és qüestió de treball, esforç, constància, unió i implicació de la colla amb la ciutat i viceversa. El lema de la colla, senzill, i directe, reflecteix aquesta voluntat: ANEM AMUNT!!!', '+34680865529', 'info@jovedevilafranca.cat', 'http://www.jovedevilafranca.cat/', 'c. Amàlia Soler, 8 Vilafranca del Penedès 08720 - Barcelona', '#85A8DD', '~/images/colles/jovesvilafranca.png'),
('Colla Jove Xiquets de Tarragona', false, 'L’any 1980 es va fer la presentació oficial, adoptant el color lila per a la camisa i el nom de Colla Jove Xiquets de Tarragona. L’el·lecció del color de la camisa va provocar un enrenou important, ja que se suposava que el color lila i el color blau marí havien estat prohibits en un pacte de sang durant la reunificació l’any 1970.', '+34639240248', 'collajove@collajove.cat', 'http://www.collajove.cat/', 'Carrer Cós del Bou, 23, Tarragona', '#957AB1', '~/images/colles/jovetarragona.png'),
('Colla Jove de l’Hospitalet' , false, null, '+34665850558', 'info@collajovelh.org', 'www.collajovelh.cat', 'Joncs, 10 - l’Hospitalet de Llobregat ', '#32CD32', '~/images/colles/joveshospitalet.png'),
('Colla Jove de Castellers de Sitges' , false, 'La desparició dels Castellers de Sitges deixa la vila orfe de vivències castelleres. El parèntesi, però, és breu, ja que Sitges no es resigna a quedar al marge del panorama casteller. L’any 1990, des de l’Agrupació de Balls Populars de Sitges s’impulsa una escola de castells que pretén ser l’embrió d’una nova colla. Després d’algunes aturades, el projecte no arriba a quallar fins el setembre del 1992, quan es realitzen al Palau del Rei Moro els primers assaigs del que haurà de ser la nova colla sitgetana. Els assaigs es mantenen amb regularitat durant tot l’hivern buscant emplaçaments còmodes i aptes, fins que l’abril del 1993, a l’eslglésia de Sant Joan, els integrants del nou grup decideixen constituir-se en colla castellera, que rep el nom de Colla Jove de Castellers de Sitges, amb camisa de color vi, en referència a la realitat (especialment històrica) vitivinícola de la vila.', '+34630472595 ', 'ribesdelpenedes@gmail.com', 'http://www.jovedesitges.cat', 'Carrer d’En Bosch, 10 (Palau del Rei Moro) 08870 - SITGES', '#660033', '~/images/colles/jovessitges.png'),
('Colla Castellera Nyerros de la Plana' , false, 'Els Nyerros de la Plana som la Colla Castellera de Manlleu. La nostra colla va ser creada al 1998 com una escissió de l’altra colla de la comarca, els Sagals d’Osona. Tot i això, no ens vam batejar fins el 14 de març de l’any següent, apadrinats pels Castellers de Barcelona. Els nostres primers assajos van ser el campus de la Universitat de Vic, però ben aviat vam trobar sostre a l’edifici de “El Femer” de l’Ajuntament de Gurb, recolzats per aquest mateix municipi. Uns anys més tard vam passar a formar part de la Vila de Manlleu, on el nostre primer local social va ser “Can Salamí”, al passeig del Ter. Finalment vam passar al Polígon la Coromina, on encara hi tenim la seu (C/ Canigó, 32, Manlleu).', '+34639614126', 'info@nyerros.cat', 'http://www.nyerros.cat/', ' C/ Canigó 35, Polígon Industrial la Coromina, 08560 Manlleu (Barcelona)', '#D5CDFC', '~/images/colles/nyerrosplana.png'),
('Colla Castellera Jove de Barcelona' , false, 'La Jove de Barcelona va néixer el 2010 amb un esperit transgressor i una filosofia urbana per a revolucionar el món dels castells. Amb un mentalitat competitiva, treballem dins el teixit associatiu de la ciutat de Barcelona per a compartir uns valors únics de la nostra cultura: força, equilibri, valor i seny, i convertir-ho en una experiència diferent. Us convidem a venir amb nosaltres!', '+34902955349', 'info@jovedebarcelona.cat', 'https://www.jovedebarcelona.cat' , 'Carrer de Sant Adrià, 20 08030 Barcelona', '#700D17', '~/images/colles/jovesbarcelona.png');